import PopupWithForm from "./PopupWithForm";
import closeIcon from '../img/CloseIcon.svg';
import React from 'react';

function EditProfilePopup({name, isOpen, onClose, title, submitButtonText, children}) {
    return (
        <div className={`popup popup_${name} ${isOpen && 'popup_open'}   close-area`}>
            <div className="popup__container">
                <button onClick={onClose}  type="button" className="button popup__button popup__button_type_close close-area" data-bs-dismiss="edit-form" aria-label="Close" data-close>
                    <img className="popup__icon popup__icon_type_close close-area" src={closeIcon} alt="крестик" />
                </button>
                <div className="popup__content">
                    <h2 className="popup__heading">{title}</h2>
                    <form className="popup__form form form_edit" name={name} noValidate>
                        {children}
                        <button type="submit" className="button form__submit" aria-label="Создать"
                                name="formSubmit">
                            {submitButtonText}
                        </button>
                    </form>
                </div>
            </div>
        </div>
    );
}


<PopupWithForm
    name='editForm'
    title='Редактировать профиль'
    isOpen={isEditProfilePopupOpen}
    onClose={closeAllPopups}
    submitButtonText="Сохранить"
>
    <label htmlFor="name-input" className="form__label">
        <input type="text" name="nameInput" id="name-input" className="form__item form__item_el_name"
               placeholder="Ваше имя" maxLength={200} minLength={2} required/>
        <span className="form__item-error name-input-error"/>
    </label>
    <label htmlFor="description-input" className="form__label">
        <input type="text" name="descriptionInput" id="description-input"
               className="form__item form__item_el_description" placeholder="О себе" maxLength={400}
               minLength={2} required/>
        <span className="form__item-error description-input-error"/>
    </label>
</PopupWithForm>

export default EditProfilePopup;
